import Foundation

class Solution {
    
    func longestPalindrome(_ s: String) -> String {
        let length = s.lengthOfBytes(using: .utf8)
        if length < 2 {
            return s
        }
        var startIndex = 0
        var maxLength = 1
        let characters = [Character](s.characters)
        for index in 0..<length {
            if length - index <= maxLength/2 {
                break
            }
            var left = index
            var right = index
            while right < length - 1 && characters[right + 1] == characters[right] {
                right = right + 1
            }
            while right < length - 1 && left > 0 && characters[right + 1] == characters[left - 1] {
                left = left - 1
                right = right + 1
            }
            let newLength = right - left + 1
            if newLength > maxLength {
                startIndex = left
                maxLength = newLength
            }
        }
        return String(characters[startIndex..<startIndex+maxLength])
    }
    
}

let solution = Solution()
print(solution.longestPalindrome("babad"))
print(solution.longestPalindrome(""))

print(solution.longestPalindrome(""))
